mapper.select:
SELECT count(*)
FROM member
WHERE month(signupDay) like #{signupDay}

SELECT mnum,id,name,pw,email,level
FROM member
WHERE del like 'N'

	
		and id like #{id}
	
	
		and email like #{email}
	
	
		and level like #{level}
	

order by mnum desc
limit #{start}, #{count}

SELECT count(*)
FROM member
WHERE del like 'N'

	
		and id like #{id}
	
	
		and email like #{email}
	
	
		and level like #{level}

mapper.update:
UPDATE member 
Set level = #{level}
WHERE mnum = #{mnum}
