mapper.select:
SELECT num, mnum, id, title, species, sex, characters, date, location, content, createdate, bdiv, isdelete, hasimg, status
FROM misstbl
WHERE isdelete = "N"

	
		AND id LIKE #{id}
	
	
		AND (title LIKE #{title} or content LIKE #{content})
	
	
		AND title LIKE #{title}
	
	
		AND content LIKE #{content}
	

ORDER BY num DESC
LIMIT #{start}, #{count}

SELECT COUNT(*) 
FROM misstbl
WHERE isdelete = "N"

	
		AND id LIKE #{id}
	
	
		AND (title LIKE #{title} or content LIKE #{content})
	
	
		AND title LIKE #{title}
	
	
		AND content LIKE #{content}

SELECT num, mnum, id, title, species, sex, characters, date, location, content, createdate, bdiv, isdelete, hasimg, status
FROM misstbl
WHERE isdelete = "N" AND num = #{num}

mapper.delete:
UPDATE misstbl 
SET isdelete = 'Y'
WHERE num = #{num}

mapper.update:
UPDATE misstbl

	title = #{title}
	, species = #{species}
	, sex = #{sex}
	, characters = #{characters}
	, date = #{date}
	, location = #{location}
	, content = #{content}
	, hasimg = #{hasimg} 
	, status = #{status}

WHERE num = #{num}
