mapper.select:
SELECT num, mnum, id, title, content, createdate, bdiv, status, del, bnum, url
FROM reporttbl

	mnum = #{mnum}
	id = #{id}

SELECT num, mnum, id, title, content, createdate, bdiv, status, del, bnum, url
FROM reporttbl

	num = #{num}
	AND mnum = #{mnum}

mapper.update:
UPDATE reporttbl

	title = #{title}
	content = #{content}

WHERE num = #{num}

mapper.delete:
UPDATE reporttbl 
SET del = 'Y'
WHERE num = #{num}
