mapper.select:
SELECT num, id, title, careName, addr, phNum, detailText, dateFrom, dateTo
FROM voluntarytbl


		WHERE careName LIKE #{careName}


		WHERE addr LIKE #{addr}


		WHERE careName LIKE #{careName} or addr LIKE #{addr}


ORDER BY num DESC
LIMIT #{start}, #{count}

SELECT count(*)
FROM voluntarytbl


		WHERE careName LIKE #{careName}


		WHERE addr LIKE #{addr}


		WHERE careName LIKE #{careName} or addr LIKE #{addr}

SELECT num, id, title, careName, addr, phNum, detailText, dateFrom, dateTo
FROM voluntarytbl

	num = #{num}
